// Data must have ".nodes with a gID". (See testData() above)
function drawD3Graph(data){
    var graphDivID = "d3Graph";
    $("#"+graphDivID).empty();

    var graph = { "nodes":[], "links":[] };
    var nodeMap = {};
    for(var i = 0; i < data.nodes.length; i++){
         var node = data.nodes[i];
         nodeMap[node.gID] = i;
        graph.nodes.push(node);
    }

    for(var i = 0; i < data.edges.length; i++){
        var link = data.edges[i];

        if(nodeMap[link.source] == undefined || nodeMap[link.target] == undefined ){
            continue;
        }

        // Ensure the edges are mapped correctly.
        // This is because the d3 library requires the edges source/target to be
        // the index of the node in the graph.nodes list.
        link.source =  nodeMap[link.source];
        link.target =  nodeMap[link.target];
        graph.links.push(link);
    }

    var width = 5000,
        height = 5000;

    var color = d3.scale.category20();

    var force = d3.layout.force()
        .charge(-120)
        .linkDistance(30)
        .size([width, height]);

    var svg = d3.select("body").append("svg")
        .attr("width", width)
        .attr("height", height);

    force.nodes(graph.nodes)
        .links(graph.links)
        .start();

    var link = svg.selectAll(".link")
        .data(graph.links)
        .enter().append("line")
        .attr("class", "link")
        .style("stroke-width", function(d) { return Math.sqrt(d.value); });

    var node = svg.selectAll(".node")
        .data(graph.nodes)
        .enter().append("circle")
        .attr("class", "node")
        .attr("r", 1)
        .style("fill", function(d) { return color(d.group); })
        .call(force.drag);

    node.append("title")
        .text(function(d) { return d.name; });

    force.on("tick", function() {
        link.attr("x1", function(d) { return d.source.x; })
            .attr("y1", function(d) { return d.source.y; })
            .attr("x2", function(d) { return d.target.x; })
            .attr("y2", function(d) { return d.target.y; });

        node.attr("cx", function(d) { return d.x; })
            .attr("cy", function(d) { return d.y; });
    });
}